import { uiActions } from "./ui-Slice";
import { cartActions } from "./cart-slice";

export const getCartData = () => {
  return async (dispatch) => {
    const getRequest = async () => {
      const response = await fetch(
        "https://react-post-e93ae-default-rtdb.firebaseio.com/cart.json"
      );
      if (!response.ok) {
        throw new Error("fetching cart data failed");
      }
      const data = await response.json();
      console.log(`data: ${data}`)
      return data;
      
    };
    try {
      const cartData=await getRequest();
      console.log(`data: ${cartData}`)
      dispatch(cartActions.replaceCart({items:cartData.items||[],totalQuantity:cartData.totalQuantity}));
    } catch (error) 
      {
        dispatch(
          uiActions.showNotifications({
            status: "error",
            title: "Error!",
            message: "Fetching cart data failed!",
          })
        );
      }
  };
};

export const sendCartData = (cartdata) => {
  return async (dispatch) => {
    dispatch(
      uiActions.showNotifications({
        status: "pending",
        title: "sending...",
        message: "sending cart data!",
      })
    );

    const sendRequest = async () => {
      const reponse = await fetch(
        "https://react-post-e93ae-default-rtdb.firebaseio.com/cart.json",
        {
          method: "PUT",
          body: JSON.stringify({items: cartdata.items,
            totalQuantity: cartdata.totalQuantity}),
        }
      );
      if (!reponse.ok) {
        throw new Error("sending cart data failed");
      }
    };
    try {
      await sendRequest();
      dispatch(
        uiActions.showNotifications({
          status: "success",
          title: "success...",
          message: "sent cart data successfully!",
        })
      );
    } catch (error) {
      dispatch(
        uiActions.showNotifications({
          status: "error",
          title: "error",
          message: "sent cart data successfullyfailed!",
        })
      );
    }
  };
};
