import React, { useState, useEffect } from "react";
import Card from "../UI/Card";
import classes from "./CheckoutForm.module.css";

const userData = [
  {
    id: 1,
    name: "Baghajatin, Kolkata, WB",
    distance: "10km",
  },
  {
    id: 2,
    name: "Garia, Kolkata, WB",
    distance: "20km",
  },
  {
    id: 3,
    name: "Sealdaha, Kolkata, WB",
    distance: "15km",
  },
  {
    id: 4,
    name: "Jadavpur, Kolkata, WB",
    distance: "25km",
  },
];

const CheckoutForm = () => {
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const [setupDate, setSetupDate] = useState("");
  const [location, setLocation] = useState("");
  const [paymentMethod, setPaymentMethod] = useState("");
  const [transportCharge, setTransportCharge] = useState("");
  const [distance, setDistance] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [eventDuration, setEventDuration] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    const data = {
      endDate,
      setupDate,
      location,
      paymentMethod,
    };
    console.log(data);
  };

const  calculateCharge=(e)=>{
    e.target.value


}
  

  useEffect(() => {
    const start = new Date(startDate).getTime();
    const end = new Date(endDate).getTime();
    const setup = new Date(setupDate).getTime();
    const now = new Date().getTime();

    if (end < setup) {
      setErrorMessage(
        "Event end date and time should not be before setup date and time"
      );
    } else if (setup > start) {
      setErrorMessage("Setup date and time should not be after event date");
    } else if ((start - setup) / (1000 * 60 * 60 * 24) > 1) {
      setErrorMessage("Setup date can not be more than 1 day before event");
    } else {
      setErrorMessage("");
      setEventDuration(
        `${(end - start) / (1000 * 60 * 60 * 24)} days ${
          ((end - start) / (1000 * 60 * 60)) % 24
        } hours`
      );
    }
  }, [startDate, endDate, setupDate]);

  return (
    <Card className={classes["parent-container"]}>
      <form className={classes["form-container"]} onSubmit={handleSubmit}>
        <label className={classes["form-label"]}>
          Event Start date and time:
          <input
            className={classes["form-input"]}
            type="datetime-local"
            value={startDate}
            onChange={(e) => setStartDate(e.target.value)}
          />
        </label>
        <label className={classes["form-label"]}>
          Event End date and time:
          <input
            className={classes["form-input"]}
            type="datetime-local"
            value={endDate}
            min={startDate}
            onChange={(e) => setEndDate(e.target.value)}
          />
        </label>
        <label className={classes["form-label"]}>
          Setup date and time:
          <input
            className={classes["form-input"]}
            type="datetime-local"
            value={setupDate}
            max={startDate}
            onChange={(e) => setSetupDate(e.target.value)}
          />
        </label>
        <label className={classes["form-label"]}>
          Location where event will be held:
          <select
            className={classes["form-input"]}
            value={location}
            onChange={calculateCharge}
          >{userData.map(item=> <option value={item.distance}>{item.name}</option>)}
          </select>
        </label>
        <label className={classes["form-label"]}>
          Payment method:
          <select className={classes["form-input"]} value={location} onChange={(e) => setPaymentMethod(e.target.value)}>
      <option value="UPI">UPI</option>
      <option value="cash">Cash</option>
      <option value="online">Online</option>
      <option value="cheque">Cheque</option>
    </select>
        </label>
        <label className={classes["form-label"]}>
          Transport charge:
          <input
            className={classes["form-input"]}
            type="text"
            value={transportCharge}
            onChange={(e) => setTransportCharge(e.target.value)}
          />
        </label>
        <label className={classes["form-label"]}>
          Distance of user selected location:
          <input
            className={classes["form-input"]}
            type="text"
            value={distance}
            onChange={(e) => setDistance(e.target.value)}
          />
        </label>
        {errorMessage && (
          <p className={classes["form-error"]}>{errorMessage}</p>
        )}
        {eventDuration && (
          <p className={classes["form-success"]}>
            Event duration: {eventDuration}
          </p>
        )}
        <button className={classes["form-button"]} type="submit">
          submit
        </button>
      </form>
    </Card>
  );
};
export default CheckoutForm;
