import React, { useState } from "react";
import Input from "../UI/Input";
import Card from "../UI/Card";
import classes from './CheckoutForm.module.css';
const Checkout = () => {
  const [eventStartDateTime, setEventStartDateTime] = useState("");
  const [eventEndDateTime, setEventEndDateTime] = useState("");
  const [setupDateTime, setSetupDateTime] = useState("");
  const [location, setLocation] = useState("");
  const [paymentMethod, setPaymentMethod] = useState("");
  const [transportCharge, setTransportCharge] = useState("");
  const [distance, setDistance] = useState("");
  const [daysHours, setDaysHours] = useState("");

  const handleEventStartDateTimeChange = (event) => {
    setEventStartDateTime(event.target.value);
  };

  const handleEventEndDateTimeChange = (event) => {
    setEventEndDateTime(event.target.value);
  };

  const handleSetupDateTimeChange = (event) => {
    setSetupDateTime(event.target.value);
  };

  const handleLocationChange = (event) => {
    setLocation(event.target.value);
  };

  const handlePaymentMethodChange = (event) => {
    setPaymentMethod(event.target.value);
  };

  const handleTransportChargeChange = (event) => {
    setTransportCharge(event.target.value);
  };

  const handleDistanceChange = (event) => {
    setDistance(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // handle form submission
     const data = {
      eventStartDateTime,
      eventEndDateTime,
      setupDateTime,
      location,
      paymentMethod,
      transportCharge,
      distance,
     
    };

    // onSubmit(data);.
    console.log(data)
  };

  return (
    <Card className={classes.form}>
      <form onSubmit={handleSubmit}>
        <Input
          label="Event Start"
          type="date"
          id="event-start"
          value={eventStartDateTime}
          onChange={handleEventStartDateTimeChange}
          isValid={true}
        />

        <Input
          label="Event End Date and Time"
          type="date"
          id="event-end-date-time"
          value={eventEndDateTime}
          onChange={handleEventEndDateTimeChange}
          isValid={true}
        />

        <Input
          label="Setup Date and Time"
          type="date"
          id="setup-date-time"
          value={setupDateTime}
          onChange={handleSetupDateTimeChange}
          isValid={true}
        />

        <div>
          <label htmlFor="location">Location where event will be held</label>
          <select id="location" onChange={handleLocationChange}>
            <option value="">Select location</option>
            <option value="Baghajatin">Baghajatin, Kolkata, WB</option>
            <option value="Garia">Garia, Kolkata, WB</option>
            <option value="Sealdaha">Sealdaha, Kolkata, WB</option>
            <option value="Jadavpur">Jadavpur, Kolkata, WB</option>
          </select>
        </div>

        <Input
          label="Payment Method"
          type="text"
          id="payment-method"
          value={paymentMethod}
          onChange={handlePaymentMethodChange}
          isValid={true}
        />

        <Input
          label="Transport Charge"
          type="number"
          id="transport-charge"
          value={transportCharge}
          onChange={handleTransportChargeChange}
          isValid={true}
        />

        <Input
          label="Distance of User Selected Location"
          type="number"
          id="distance"
          value={distance}
          onChange={handleDistanceChange}
          isValid={true}
        />

        <button type="submit">Make Enquiry</button>
      </form>
    </Card>
  );
};

export default Checkout;
